mapper.select:
SELECT productId FROM purchase
WHERE userId = #{userId}

    AND createdAt >= #{start}


    AND createdAt <= #{end}


SELECT * FROM review
WHERE userId = #{userId}
ORDER BY reviewId DESC
    LIMIT #{reviewPerPage} OFFSET ${(pageNo-1) * reviewPerPage}

SELECT total / numOfReview
FROM rating
WHERE productId = #{productId}
